import React from 'react';
import { Link } from 'react-router-dom'
import { Menu } from '@headlessui/react'
export function Language(props) {
    return (
        <div className="px-1 py-1 ">
            <Menu.Item>
                {({ active }) => (
                    <button
                        className={`${active ? ' flex justify-between text-1xl' : 'text-gray-900 flex justify-between'
                            } group flex rounded-md items-center w-full px-2 py-2 text-sm`}
                    >

                        {props.name}
                        {active ? (
                            <EditInactiveIcon
                                className="w-5 h-5 mr-2"
                                aria-hidden="true"
                            />
                        ) : (
                            <EditInactiveIcon
                                className="w-5 h-5 mr-2"
                                aria-hidden="true"
                            />
                        )}
                    </button>
                )}
            </Menu.Item>
        </div>
    );
}

export function Start(props) {
    return (
        <div className="px-1 py-1 ">
            <Menu.Item>
                {({ active }) => (
                    <button

                        className={`${active ? ' flex justify-between text-1xl text-white' : 'text-base-600 flex justify-between'
                            } group flex rounded-md items-center w-full px-2 py-2 text-sm`}
                    >

                        <Link to={props.url ? props.url : "/"} className="hover:text-current hover:no-underline"> {props.name}</Link>
                        {active ? (
                            <ArrowactiveIcon
                                aria-hidden="true"
                            />
                        ) : (
                            <ArrowactiveIcon
                                aria-hidden="true"
                            />
                        )}
                    </button>
                )}
            </Menu.Item>
        </div>
    );
}


export function Foundation(props) {
    return (
        <div className="px-1 py-1 ">
            <Menu.Item>
                {({ active }) => (
                    <button
                        className={`${active ? ' flex justify-between text-1xl text-white  text-sm' : 'text-base-600 flex justify-between'
                            } group flex rounded-md items-center w-full px-2 py-2 text-sm `}
                    >
                        <a href={props.url} target="blank">{props.name}</a>
                        {active ? (
                             props.icon ? props.icon : <FaundationInactiveIcon
                                aria-hidden="true"
                            />
                        ) : (
                            props.icon ? props.icon : <FaundationInactiveIcon
                                aria-hidden="true"
                            />
                        )}
                    </button>
                )}
            </Menu.Item>
        </div>
    );
}


